import argparse

from toolium11paths.testlink.add_features_to_testlink import (add_feature_cli_definition, add_features_main)
from toolium11paths.behave.behave_runner import (behave_runner_cli_definition, behave_runner_cli)


def toolium_cli():
    parser = argparse.ArgumentParser()
    subparsers = parser.add_subparsers(help='Toolium CLI features')

    behave_runner_parser = subparsers.add_parser('behave-runner', help="Custom runner for Behave's features")
    behave_runner_cli_definition(behave_runner_parser)
    behave_runner_parser.set_defaults(func=behave_runner_cli)

    add_features_runner = subparsers.add_parser('add-features', help="Import Behave's features as TC in TestLink")
    add_feature_cli_definition(add_features_runner)
    add_features_runner.set_defaults(func=add_features_main)

    args = parser.parse_args()
    if args.func:
        args.func(args)


if __name__ == '__main__':
    print("""
  _______          _ _                    _____ _      _____
 |__   __|        | (_)                  / ____| |    |_   _|
    | | ___   ___ | |_ _   _ _ __ ___   | |    | |      | |
    | |/ _ \ / _ \| | | | | | '_ ` _ \  | |    | |      | |
    | | (_) | (_) | | | |_| | | | | | | | |____| |____ _| |_
    |_|\___/ \___/|_|_|\__,_|_| |_| |_|  \_____|______|_____|

    """)
    toolium_cli()
